import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { getItems, handleShow, deleteItem } from "../actions/itemActions";
import { logout } from "../actions/authActions";
import { Paper } from "@material-ui/core";
import { Button } from "@material-ui/core";
import Typography from "@material-ui/core/Typography";

class Dashboard extends Component {
  onLogoutClick = e => {
    e.preventDefault();
    this.props.logoutUser();
  };
  componentDidMount() {
    this.props.getItems();
  }
  onLogout = () => {
    this.props.logout();
  };
  onDeleteClick = id => {
    this.props.deleteItem(id);
    console.log(id);
  };
  onHandleShow = id => {
    this.props.handleShow(id);
    console.log(id);
  };

  render() {
    function getMyDay(day) {
      const date1 = new Date(day);
      const dayOfWeekIndex = date1.getDay();
      const dayNames = ["Mon", "Tue", "Wed", "Thur", "Fri", "Sat", "Sund"];
      return dayNames[dayOfWeekIndex];
    }
    function getMyMonth(month) {
      const month1 = new Date(month);
      const dayOfMonthIndex = month1.getMonth();
      const monthNames = [
        "Jan",
        "Feb",
        "Mar",
        "Apr",
        "May",
        "June",
        "July",
        "Aug",
        "Sept",
        "Oct",
        "Nov",
        "Dec"
      ];
      return monthNames[dayOfMonthIndex];
    }
    const { items } = this.props.item;
    const { user } = this.props.auth;
    const { isAuthenticated } = this.props.auth;
    // items.map((each, i) => console.log(items[i].showMore));

    return (
      <div>
        {isAuthenticated === true ? (
          <div>
            <button onClick={this.onLogout}>Log me out</button>
            <h1>Hi {user.name}</h1>

            <div>this is your dashboard</div>
            {items.map(({ weathers, _id, newID }, i) =>
              newID === user._id
                ? weathers && (
                    <Paper className="each-weather" key={_id}>
                      <Button
                        className="weather-button"
                        variant="outlined"
                        color="secondary"
                        onClick={() => this.onDeleteClick(_id)}
                      >
                        Delete from list
                      </Button>
                      <br />
                      <Typography
                        className="each-weather-title"
                        variant="h3"
                        component="h3"
                      >
                        {weathers[0].res.title && weathers[0].res.title}
                      </Typography>
                      <Typography variant="h6" component="h6">
                        Timezone: {weathers[0].res.timezone}
                      </Typography>
                      <Typography variant="h6" component="h6">
                        Time: {weathers[0].res.time.slice(11, 16)}
                      </Typography>
                      <Typography variant="h6" component="h6">
                        Temperature:{" "}
                        {weathers[0].res.consolidated_weather[0].the_temp &&
                          weathers[0].res.consolidated_weather[0].the_temp.toFixed(
                            1
                          )}{" "}
                        degrees celcius
                      </Typography>

                      <img
                        className="weather-image"
                        src={`https://www.metaweather.com/static/img/weather/${weathers[0].res.consolidated_weather[0].weather_state_abbr}.svg`}
                        alt="weather state"
                      />
                      <Typography variant="h6" component="h6">
                        Weather Conditions:{" "}
                        {
                          weathers[0].res.consolidated_weather[0]
                            .weather_state_name
                        }
                      </Typography>
                      <Typography variant="h6" component="h6">
                        Predictability:{" "}
                        {weathers[0].res.consolidated_weather[0]
                          .predictability &&
                          weathers[0].res.consolidated_weather[0]
                            .predictability}
                        %
                      </Typography>

                      {items[i].showMore === false ? (
                        <Button
                          className="weather-button"
                          variant="outlined"
                          color="primary"
                          onClick={() => this.onHandleShow(_id)}
                        >
                          Show more
                        </Button>
                      ) : (
                        <div>
                          <hr />
                          <div className="show-more-content">
                            {weathers[0].res.consolidated_weather.map(
                              (weather, i) => (
                                <div className="show-more-each" key={i}>
                                  {i === 0 ? (
                                    <Typography variant="h6" component="h6">
                                      Tomorrow
                                    </Typography>
                                  ) : (
                                    <Typography variant="h6" component="h6">
                                      {getMyDay(
                                        weathers[0].res.consolidated_weather[i]
                                          .applicable_date
                                      )}{" "}
                                      {parseInt(
                                        weathers[0].res.consolidated_weather[
                                          i
                                        ].applicable_date.slice(8, 10)
                                      ) < 10
                                        ? weathers[0].res.consolidated_weather[
                                            i
                                          ].applicable_date.slice(9, 10)
                                        : weathers[0].res.consolidated_weather[
                                            i
                                          ].applicable_date.slice(8, 10)}{" "}
                                      {getMyMonth(
                                        weathers[0].res.consolidated_weather[i]
                                          .applicable_date
                                      )}
                                    </Typography>
                                  )}
                                  <img
                                    className="weather-image"
                                    src={`https://www.metaweather.com/static/img/weather/${weathers[0].res.consolidated_weather[i].weather_state_abbr}.svg`}
                                    alt="weather state"
                                  />
                                  <Typography variant="h6" component="h6">
                                    {
                                      weathers[0].res.consolidated_weather[i]
                                        .weather_state_name
                                    }
                                  </Typography>
                                  <Typography variant="h6" component="h6">
                                    {weathers[0].res.consolidated_weather[i]
                                      .the_temp &&
                                      weathers[0].res.consolidated_weather[
                                        i
                                      ].the_temp.toFixed(1)}
                                    C
                                  </Typography>
                                  <Typography variant="h6" component="h6">
                                    Predictability:{" "}
                                    {weathers[0].res.consolidated_weather[i]
                                      .predictability &&
                                      weathers[0].res.consolidated_weather[i]
                                        .predictability}
                                    %
                                  </Typography>
                                </div>
                              )
                            )}
                          </div>
                          <Button
                            className="weather-button"
                            variant="outlined"
                            color="primary"
                            onClick={() => this.onHandleShow(_id)}
                          >
                            Show less
                          </Button>
                        </div>
                      )}
                    </Paper>
                  )
                : null
            )}
          </div>
        ) : (
          <div>please login </div>
        )}
      </div>
    );
  }
}
Dashboard.propTypes = {
  getItems: PropTypes.func.isRequired,
  deleteItem: PropTypes.func.isRequired,
  handleShow: PropTypes.func.isRequired,
  logout: PropTypes.func.isRequired
  // item: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  item: state.item,
  auth: state.auth
});

export default connect(mapStateToProps, {
  getItems,
  deleteItem,
  handleShow,
  logout
})(Dashboard);
