        />
      </div>

      <h3>Total found: {weatherData.length} </h3>
      {initialQuery.length > 0 && weatherData.length !== initialQuery.length ? (
        <img
          src="https://media.giphy.com/media/l31p1SkNXGz3l1nwwu/giphy.gif"
